import React, {useReducer} from 'react';
import MenuContext from './MenuContext';
import MenuReducer from './MenuReducer';
import {CAMBIAR_MENU} from '../../types';

const MenuState = props => {
    
    /*
    const menus = [
        {id: 1, nombre: 'MiEquipo', estado: true},
        {id: 2, nombre: 'Jugadores', estado: false},
        {id: 3, nombre: 'Plantel', estado: false},
        {id: 4, nombre: 'Partidos', estado: false},
        {id: 5, nombre: 'Formacion', estado: false},
        {id: 6, nombre: 'Entrenamientos', estado: false},
        {id: 7, nombre: 'Reportes', estado: false}
    ]
    */

    const initialSate = {
        //menus : [],
        seleccion : 7
    }

    //dispatch para ejecutar las acciones
    const [state, dispatch] = useReducer(MenuReducer, initialSate)

    //serie de funciones para el crud
    const cambiarMenu = (sel) => {
        dispatch ({
            type: CAMBIAR_MENU,
            payload: sel
        })
    }

    return (
        <MenuContext.Provider
            value={{
                seleccion : state.seleccion,
                cambiarMenu
            }}
        >
            {props.children}
        </MenuContext.Provider>
    )
}
export default MenuState;
