import React, { Fragment, useState} from 'react';

const NuevoEquipo = () => {

    //State para el equipo
    const [equipo, guardarEquipo] = useState({
        nombre: ''
    });

    //Extraer nombre del state equipo
    const {nombre} = equipo;

    //Lee el contenido del input
    const onChangeEquipo = e => {
        guardarEquipo ({
            ...equipo,
            [e.target.name] : e.target.value
        })
    }

    //cuando el usuario envia un Equipo
    const onSubmitProyecto = e => {
        e.preventDefault();

        //Cuando enviemos el form, esto es lo que haremos:
        //validar proyecto
        //agregar al state
        //reiniciar el form
    }

    return (
        <Fragment>
            <button
                type="button"
                className="btn btn-block btn-primario"
            >Nuevo Equipo</button>
            <form className ="formulario-nuevo-proyecto"
                onSubmit={onSubmitProyecto}
            >
                <input
                    type="text"
                    className="input-text"
                    placeholder="Nombre Equipo"
                    name="nombre"
                    value={nombre}
                    onChange={onChangeEquipo}
                />
                <input
                    type="submit"
                    className="btn btn-block btn-primario"
                    value="Agregar Equipo"
                />
            </form>
        </Fragment> 
    );
}
export default NuevoEquipo;
