import React, {useState} from 'react';

export default function Menu() {

    //State para elegir el menu
    //const [equipo, cambiarEquipo] = useState(true)({
    //    
    //});

    const [equipo, guardarEquipo] = useState("")

    const handleClick = n => {
        guardarEquipo(n)
    }

    /*
    <button onClick={() => cambiarEquipo(!equipo)} type="button">
                Jugadores
            </button>
    <a href="#"
                onClick = {handleClick}>Jugadores
            </a>
    */

    return (
        <header className="app-menu">
            <nav className="nav-principal">
                 <a href="#">Equipo</a>
            </nav>
            <a href="#"
                onClick = {handleClick(2)}>Jugadores
            </a>
            <a href="#">Plantel</a>
            <a href="#">Partidos</a>
            <a href="#">Formacion</a>
            <a href="#">Entrenamientos</a>
            <a href="#">Reportes</a>
        </header>
    );
}
