import React, { Fragment, useEffect, useState } from 'react';
import Administrar from './Administrar';
import Entrenamientos from './Entrenamientos';
import MenuForm from './MenuForm';

const ListadoTareas = (eleccion) => {

  //state para tener el nombre de la tarea que elegimos
  //con el boton que presionamos y pasarlo al return
  
  const [menu, cambiarMenu] = useState({
    estadoMenu1: ''
  });

  let estadoMenu = eleccion;

  console.log('estado menu al iniciar: ' + estadoMenu);

  useEffect( () => {
    switch(eleccion) {
      case 'Administrar':
        return (
          <Administrar />
        )
      case 'Entrenamientos':
        return (
          <Entrenamientos />
        )
      case '':
        return (
          <MenuForm />
        )
    }
    console.log('estado menu aca: ' + estadoMenu);
  }, [eleccion]  );


  /*
  const [Administrar, cambiarEstadoAdministrar] = useState(false);
  const [Entrenamientos, cambiarEstadoEntrenamientos] = useState(false);
  const [Organizacion, cambiarEstadoOrganizacion] = useState(false);
  const [Partidos, cambiarEstadoPartidos] = useState(false);
  const [Partidos, cambiarEstadoPartidos] = useState(false);
  const [Plantel, cambiarEstadoPlantel] = useState(false);
  const [Reportes, cambiarEstadoReportes] = useState(false);
  const [Respaldo, cambiarEstadoRespaldo] = useState(false);
  const [Rivales, cambiarEstadoRivales] = useState(false);
  const [Sugerir, cambiarEstadoSugerir] = useState(false);
  */

  /*
  switch(estadoMenu) {
    case 'Administrar':
      return (
        <Administrar />
      )
    case 'Entrenamientos':
      return (
        <Entrenamientos />
      )
    case '':
      return (
        <Menu />
      )
  }

    
    case 'Organizacion':
       eleccionTarea = 'Organizacion';
       break;
    case 'Partidos':
      eleccionTarea = 'Partidos';
      break;
    case 'Plantel':
      eleccionTarea = 'Plantel';
      break;
    case 'Reportes':
      eleccionTarea = 'Reportes';
      break;
    case 'Respaldo':
      eleccionTarea = 'Respaldo';
      break;
    case 'Rivales':
      eleccionTarea = 'Rivales';
      break;
    case 'Sugerir':
      eleccionTarea = 'Sugerir';
      break;
    case '':
      eleccionTarea = 'Botones';
      break;
    default:
      eleccionTarea = 'Botones';
      break;
  } */


  return (
    <Fragment>
      <MenuForm />
      <button
        type="button"
        className="btn btn-eliminar"
        >Eliminar Equipo</button>
    </Fragment>
  );
}
export default ListadoTareas;
