import React, {useReducer} from 'react';
import EquipoContext from './EquipoContext';
import EquipoReducer from './EquipoReducer';
import {
    FORMULARIO_EQUIPO,
    OBTENER_EQUIPOS,
    AGREGAR_EQUIPO
} from '../../types';

const EquipoState = props => {
    
    const equipos = [
        {id: 1, nombre: 'Equipo1'},
        {id: 2, nombre: 'Equipo2'},
        {id: 3, nombre: 'Equipo3'},
        {id: 4, nombre: 'MERN'}
    ]
   
    const initialSate = {
        equipos : [],
        formulario : false
    }

    //dispatch para ejecutar las acciones
    const [state, dispatch] = useReducer(EquipoReducer, initialSate)

    //serie de funciones para el crud
    const mostrarFormulario = () => {
        dispatch ({
            type: FORMULARIO_EQUIPO
        })
    }

    console.log(equipos);
    //Obtener los proyectos
    const obtenerEquipos = () => {
        dispatch({
            type: OBTENER_EQUIPOS,
            payload: equipos
        })
    }

    return (
        <EquipoContext.Provider
            value={{
                equipos: state.equipos,
                formulario: state.formulario,
                mostrarFormulario,
                obtenerEquipos
            }}
        >
            {props.children}            
        </EquipoContext.Provider>
    )
}
export default EquipoState;
